AND B.TITLE LIKE CONCAT('%', #{keyword}, '%')
AND B.BOARD_TYPE IN(
#{value}
)

mapper.update:
UPDATE T_BOARD
SET
	TITLE = #{title},
	CONTENTS = #{contents}
WHERE BOARD_SEQ = #{boardSeq}
